import React from 'react'
import { useParams } from 'react-router-dom'
import { useState,useEffect } from 'react'

const SingleProduct = () => {
    const [product,setProduct]= useState({});
    const params = useParams();
    console.log(params)
    useEffect(()=>{
        fetch(`https://star-spark-pasta.glitch.me/api/products/${params._id}`)
        .then(res =>res.json())
        .then(product =>{
            console.log(product)
            setProduct(product)
        })
    },[])
    
  return (
    <>
     <div className='container mx-auto mt-12'>
        <button className='mb-12 font-bold'>Back</button>
        <div className='flex'>
            <img src={product.image} alt="pizza"></img>
            <div className='ml-16'>
                <h1 className='text-xl font-bold'>{product.name}</h1>
                <div className='text-md'>small</div>
                <div className='font-bold mt-2'>$ 600</div>
                <button className='bg-yellow-500 py-1 px-8 rounded-full font-bold mt-4'>Add To Cart</button>

            </div>
        </div>
     </div>
    </>
  )
}

export default SingleProduct
